import React, { useEffect, useState } from 'react';
import PartService from '../services/PartService';

function PartList() {
  const [parts, setParts] = useState([]);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    PartService.getAllParts()
      .then(res => {
        setParts(res.data || []);
      })
      .catch(err => {
        console.error('Failed to load parts', err);
        setParts([]);
      })
      .finally(() => setLoading(false));
  }, []);

  return (
    <div className="wc-table">
      <div className="wc-page-header">
        <h2 className="wc-page-title">Part List</h2>
        <div className="wc-actions">
          <button type="button" className="btn btn-sm btn-outline-primary">Create Part</button>
          <button type="button" className="btn btn-sm btn-outline-secondary ms-2">Export List</button>
        </div>
      </div>

      {loading ? (
        <div className="wc-empty">Loading partsâ€¦</div>
      ) : parts.length === 0 ? (
        <div className="wc-empty">No parts yet. Click Create Part to add one.</div>
      ) : (
        <table className="table table-sm">
          <thead>
            <tr>
              <th>ID</th>
              <th>Name</th>
              <th>Version</th>
              <th>State</th>
            </tr>
          </thead>
          <tbody>
            {parts.map(p => (
              <tr key={p.id ?? p.partNumber}>
                <td>{p.partNumber}</td>
                <td>{p.name}</td>
                <td>{p.version}</td>
                <td>{p.lifecycleState}</td>
              </tr>
            ))}
          </tbody>
        </table>
      )}
    </div>
  );
}

export default PartList;
