import React, { useState } from 'react';
import Layout from './components/Layout';
import PartList from './components/PartList';
import CreatePartPanel from './components/CreatePartPanel';
import './wc-theme.css';
import PartService from './services/PartService';

function App() {
  const [showCreate, setShowCreate] = useState(false);
  const [reloadFlag, setReloadFlag] = useState(false);

 const [editingPart, setEditingPart] = useState(null);

const handleEditPart = (part) => {
  setEditingPart(part);   
  setShowCreate(true);    
};
const handleCreated = () => {
  setShowCreate(false);
  setEditingPart(null);
  setReloadFlag(!reloadFlag);
};

const handleDeletePart = (id) => {
  if (!window.confirm('Are you sure you want to delete this part?')) {
    return;
  }
  PartService.deletePart(id)
    .then(() => setReloadFlag(!reloadFlag));

};

return (
  <Layout>
    <PartList
      onCreateClick={() => setShowCreate(true)}
      onEditClick={handleEditPart}
      onDeleteClick={handleDeletePart}
      reloadFlag={reloadFlag}
    />
    {showCreate && (
      <CreatePartPanel
        onClose={() => { setShowCreate(false); setEditingPart(null); }}
        onCreated={handleCreated}
        existingPart={editingPart}
      />
    )}
  </Layout>
);
}

export default App;
